var EXECUTION_STATUS_EVENT_FOR_DELIVERY_ITEM = "com.lbngttsamples.gtt.app.sof.DeliveryItem.DeliveryItemPOD";
var EXECUTION_STATUS_EVENT_FOR_SHIPMENT = "com.lbngttsamples.gtt.app.sof.Shipment.POD";

var obj = toObject("{}");
var needForward = false;
?"===> eventType";
?actualEvent.eventType;
?"===> trackedProcess";
?trackedProcess;
if("com.lbngttsamples.gtt.app.sof.DeliveryItem.DelayForTest" == actualEvent.eventType) {
    obj = toObject("{}");
    obj.processStatus_code = "DELAYED";
    ?trackedProcess.id;
    updateTPProperties(trackedProcess, obj);
    
} else if("com.lbngttsamples.gtt.app.sof.SalesOrder.SalesOrderEvent" == actualEvent.eventType) {
    var userPlaceholders0 = toObject("{}");
    var appPlaceholders0 = toObject("{}");
    var directMailTemp0 = reminderService.generateDirectMailTemplate();
    var salesOrderGUID0 = trackedProcess.id;
    
    userPlaceholders0.recipients1 = "johnny.zong@sap.com";
    userPlaceholders0.salesOrderNo = trackedProcess.salesOrderNo;
    userPlaceholders0.salesOrderGUID = salesOrderGUID0;
    
    appPlaceholders0.solutionOwner_BpName = "This is BP name";
    appPlaceholders0.App_Placeholder_1 = "This is APP Place holder 1";
    
    directMailTemp0.templateId = "invite";
    directMailTemp0.recipients = ["jinming.wang@sap.com", "dexian.li@sap.com", "joe.xia@sap.com", "guo.chen@sap.com"];
    directMailTemp0.userdefinedPlaceholders = userPlaceholders0;
    directMailTemp0.applicationdefinedPlaceholders = appPlaceholders0;
    directMailTemp0.post();
    
} else if("com.lbngttsamples.gtt.app.sof.SalesOrderItem.SOItemDelay" == actualEvent.eventType) {
    obj = toObject("{}");
    obj.processStatus_code = "DELAYED";
    if(trackedProcess.delayedQuantity == null) {
        obj.delayedQuantity = actualEvent.quantity;
    }
    else {
        obj.delayedQuantity = trackedProcess.delayedQuantity + actualEvent.quantity;
    }
    updateTPProperties(trackedProcess, obj);
    
    var userPlaceholders1 = toObject("{}");
    var directMailTemp1 = reminderService.generateDirectMailTemplate();
    var salesOrderGUID1 = trackedProcess.salesOrder_id;
    
    userPlaceholders1.recipients1 = "ziyi.jiang@sap.com";
    userPlaceholders1.salesOrderNo = trackedProcess.salesOrderNo;
    userPlaceholders1.itemNo = trackedProcess.itemNo;
    userPlaceholders1.salesOrderGUID = salesOrderGUID1;
    userPlaceholders1.business_time = actualEvent.actualBusinessTimestamp;
    userPlaceholders1.timezone = actualEvent.actualBusinessTimeZone;
    
    directMailTemp1.templateId = "gtt_delay";
    directMailTemp1.templateSource = "USERTEMPLATE";
    directMailTemp1.locale = "en";
    directMailTemp1.recipients = ["jinming.wang@sap.com", "dexian.li@sap.com", "joe.xia@sap.com", "guo.chen@sap.com"];
    directMailTemp1.userdefinedPlaceholders = userPlaceholders1;
    directMailTemp1.templateRecipientIgnore = false;
    directMailTemp1.post();
    
    
} else if (trackedProcess != null && "com.lbngttsamples.gtt.app.sof.DeliveryItem.DeliveryItem" == trackedProcess.trackedProcessType) {
    ?"entering DeliveryItem processing";
    if ("com.lbngttsamples.gtt.app.sof.DeliveryItem.DeliveryItemEvent" == actualEvent.eventType) {
		?"entering DeliveryItemEvent";
		obj = toObject("{}");
		if (trackedProcess.initialPlannedDate == null) {
			obj.initialPlannedDate = trackedProcess.revisedPlannedDate;
		}
        if (trackedProcess.executionStatus_code == null) {
			obj.executionStatus_code = "NOT_STARTED";
		}
        
        updateTPProperties(trackedProcess, obj);
        needForward = false;
        
    } else if (EXECUTION_STATUS_EVENT_FOR_DELIVERY_ITEM == actualEvent.eventType) {
        ?"===> entering DeliveryItemPOD";
        obj = toObject("{}");
        obj.executionStatus_code = "COMPLETED";
        updateTPProperties(trackedProcess, obj);
        needForward = true;
        
    } else if (trackedProcess.executionStatus_code != "COMPLETED") {
        if (EXECUTION_STATUS_EVENT_FOR_SHIPMENT == actualEvent.eventType) {
            ?"===> entering Shipment.POD of DeliveryItem";
            var plannedEvents = trackedProcess.plannedEvents;
            var allShipmentPODReported = true; 
            var found = false;
            var i = 0;
            while (i < plannedEvents.size() && allShipmentPODReported) {
                if (plannedEvents[i].eventType == "com.lbngttsamples.gtt.app.sof.Shipment.POD") {
                    found = true;
                    if (plannedEvents[i].eventStatus_code != "REPORTED" 
                        && plannedEvents[i].eventStatus_code != "EARLY_REPORTED" 
                        && plannedEvents[i].eventStatus_code != "LATE_REPORTED") {
                    
                        allShipmentPODReported = false;
                    }    
                }
                i = i + 1;
            }
            
            if (allShipmentPODReported && found) {
                obj = toObject("{}");
                obj.executionStatus_code = "COMPLETED";
                updateTPProperties(trackedProcess, obj); 
            }
            
            needForward = true;
            
        } else if ("com.lbngttsamples.gtt.app.sof.DeliveryItem.Picking" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.sof.DeliveryItem.Packing" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.sof.DeliveryItem.GoodsIssued" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.sof.Shipment.Departure" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.sof.Shipment.Arrival" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.sof.Shipment.LoadingStart" == actualEvent.eventType || 
                "com.lbngttsamples.gtt.app.sof.Shipment.LoadingEnd" == actualEvent.eventType) {
            
            ?"===> entering other events of Delivery Item without Delay and LocationUpdate";    
            obj = toObject("{}");
            obj.executionStatus_code = "IN_TRANSIT";
            updateTPProperties(trackedProcess, obj); 
            needForward = true;
        }
        
    } 
    
    if ("com.lbngttsamples.gtt.app.sof.Shipment.Delay" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.LocationUpdate" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.DeliveryItem.Picking" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.DeliveryItem.Packing" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.DeliveryItem.DeliveryItemPOD" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Delivery.GoodsIssued" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Departure" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Arrival" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.LoadingStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.LoadingEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.POD" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.GateInStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.GateInEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.GateOutStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.GateOutEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.UnloadingStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.UnloadingEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Stuffing" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.UnStuffing" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Return" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.POPU" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.CheckIn" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Receive" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Delivered" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Coupling" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Decoupling" == actualEvent.eventType || 
			"com.lbngttsamples.gtt.app.sof.Shipment.OutForDelivery" == actualEvent.eventType) {
        ?"===> entering other events of DeliveryItem with Delay and LocationUpdate";  
		var lastCorrelatedEvent = trackedProcess.getLastCorrelatedEventForCurrentTPIncludeEventTypes(
								[
								"com.lbngttsamples.gtt.app.sof.DeliveryItem.Picking",
								"com.lbngttsamples.gtt.app.sof.DeliveryItem.Packing",
								"com.lbngttsamples.gtt.app.sof.DeliveryItem.DeliveryItemPOD",
								"com.lbngttsamples.gtt.app.sof.Delivery.GoodsIssued",
								"com.lbngttsamples.gtt.app.sof.Shipment.Departure",
								"com.lbngttsamples.gtt.app.sof.Shipment.Arrival",
								"com.lbngttsamples.gtt.app.sof.Shipment.LoadingStart",
								"com.lbngttsamples.gtt.app.sof.Shipment.LoadingEnd",
								"com.lbngttsamples.gtt.app.sof.Shipment.POD",
								"com.lbngttsamples.gtt.app.sof.Shipment.GateInStart",
								"com.lbngttsamples.gtt.app.sof.Shipment.GateInEnd",
								"com.lbngttsamples.gtt.app.sof.Shipment.GateOutStart",
								"com.lbngttsamples.gtt.app.sof.Shipment.GateOutEnd",
								"com.lbngttsamples.gtt.app.sof.Shipment.UnloadingStart",
								"com.lbngttsamples.gtt.app.sof.Shipment.UnloadingEnd",
								"com.lbngttsamples.gtt.app.sof.Shipment.Stuffing",
								"com.lbngttsamples.gtt.app.sof.Shipment.UnStuffing",
								"com.lbngttsamples.gtt.app.sof.Shipment.Return",
								"com.lbngttsamples.gtt.app.sof.Shipment.POPU",
								"com.lbngttsamples.gtt.app.sof.Shipment.CheckIn",
								"com.lbngttsamples.gtt.app.sof.Shipment.Receive",
								"com.lbngttsamples.gtt.app.sof.Shipment.Delivered",
								"com.lbngttsamples.gtt.app.sof.Shipment.Coupling",
								"com.lbngttsamples.gtt.app.sof.Shipment.Decoupling",
								"com.lbngttsamples.gtt.app.sof.Shipment.OutForDelivery"]);
		
		if (lastCorrelatedEvent != null && lastCorrelatedEvent.id != trackedProcess.lastCorrelatedEventId) {
			?"===> lastCorrelatedEvent";
			?lastCorrelatedEvent;
			obj = toObject("{}");
            obj.lastCorrelatedEventId = lastCorrelatedEvent.id;
            updateTPProperties(trackedProcess, obj);
			needForward = true;
		}
							
        if (trackedProcessBeforeEventPost != null && trackedProcessBeforeEventPost.processStatus_code != trackedProcess.processStatus_code) {
            ?"===>processStatus changed to";
            ?trackedProcess.processStatus_code;
            needForward = true;
        }
    }
    
    ?"==>DeliveryItem done.";
    
    
} else if (trackedProcess != null && "com.lbngttsamples.gtt.app.sof.Delivery.Delivery" == trackedProcess.trackedProcessType) {
    ?"entering Delivery processing";
	var plannedEvents = trackedProcess.plannedEvents;
	var shipmentTPs = trackedProcess.shipmentTPs;
	var trackingIds = trackedProcess.trackingIds;
	?"plannedEvents:";
	?plannedEvents;
	?"shipmentTPs:";
	?shipmentTPs;
	?"trackingIds:";
	?trackingIds;
	
	obj = toObject("{}");
	var i = 0;
	var j = 0;
	var k = 0;
	var foundShipment = false;
	var foundObservedTrackingId = false;
	if ("com.lbngttsamples.gtt.app.sof.Delivery.DeliveryEvent" == actualEvent.eventType) {
		while (i < plannedEvents.size()) {
			if ("com.lbngttsamples.gtt.app.sof.Shipment.Departure" == plannedEvents[i].eventType) {
				j = 0;
				foundShipment = false;
				while (j < shipmentTPs.size() && !foundShipment) {
					if (shipmentTPs[j].firstStop == plannedEvents[i].eventMatchKey) {
						k = 0;
						foundObservedTrackingId = false;
						while (k < trackingIds.size() && !foundObservedTrackingId) {
							if (trackingIds[k].observedProcess_id == shipmentTPs[j].shipment_id) {
								if (plannedEvents[i].plannedBusinessTimestamp != null) {
							        trackingIds[k].validFrom = plannedEvents[i].plannedBusinessTimestamp.minusMinutes(90);
							    }
								obj.trackingIds = trackingIds;
								updateTPProperties(trackedProcess, obj);
								?"validFrom updated successfully to:";
								?trackingIds[k].validFrom;
								foundObservedTrackingId = true;
							}
							k = k + 1;
						}
						foundShipment = true;
					}
					j = j + 1;
				}
			}
			i = i + 1;
		}
	}
	
	if ("com.lbngttsamples.gtt.app.sof.Shipment.POD" == actualEvent.eventType) {
		i = 0;
		var found = false;
		while (i < plannedEvents.size() && !found) {
			if ("com.lbngttsamples.gtt.app.sof.Shipment.POD" == plannedEvents[i].eventType
			    && plannedEvents[i].eventMatchKey == actualEvent.eventMatchKey) {
				j = 0;
				while (j < shipmentTPs.size() && !found) {
					if (shipmentTPs[j].lastStop == plannedEvents[i].eventMatchKey) {
						k = 0;
						while (k < trackingIds.size() && !found) {
							if (trackingIds[k].observedProcess_id == shipmentTPs[j].shipment_id) {
								trackingIds[k].validTo = actualEvent.actualBusinessTimestamp;
								obj.trackingIds = trackingIds;
								updateTPProperties(trackedProcess, obj);
								?"validTo updated successfully to:";
								?trackingIds[k].validTo;
								found = true;
							}
							k = k + 1;
						}
					}
					j = j + 1;
				}
			}
			i = i + 1;
		}
	}
	
	?"==>Delivery done.";
	
	
} else if (trackedProcess != null && "com.lbngttsamples.gtt.app.sof.Shipment.Shipment" == trackedProcess.trackedProcessType) {
	?"entering Shipment processing";
    if ("com.lbngttsamples.gtt.app.sof.Shipment.ShipmentEvent" == actualEvent.eventType) {
        ?"===> entering ShipmentEvent";
		if (trackedProcess.executionStatus_code == null) {
			obj = toObject("{}");
			obj.executionStatus_code = "NOT_STARTED";
			updateTPProperties(trackedProcess, obj);
			
		} 
    } else if ("com.lbngttsamples.gtt.app.sof.Shipment.POD" == actualEvent.eventType)  {
		?"===> entering Shipment.POD";	
		if (trackedProcess.executionStatus_code != "COMPLETED") {
			var plannedEvents = trackedProcess.plannedEvents;
            var allShipmentPODReported = true; 
            var found = false;
            var i = 0;
            while (i < plannedEvents.size() && allShipmentPODReported) {
                if (plannedEvents[i].eventType == "com.lbngttsamples.gtt.app.sof.Shipment.POD") {
                    found = true;
                    if (plannedEvents[i].eventStatus_code != "REPORTED" 
                        && plannedEvents[i].eventStatus_code != "EARLY_REPORTED" 
                        && plannedEvents[i].eventStatus_code != "LATE_REPORTED") {
                    
                        allShipmentPODReported = false;
                    }    
                }
                i = i + 1;
            }
            
            if (allShipmentPODReported && found) {
                obj = toObject("{}");
                obj.executionStatus_code = "COMPLETED";
                updateTPProperties(trackedProcess, obj); 
            }
			
		} 
		
	} else if ("com.lbngttsamples.gtt.app.sof.Shipment.Departure" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.Arrival" == actualEvent.eventType || 
			"com.lbngttsamples.gtt.app.sof.Shipment.CheckIn" == actualEvent.eventType ||
            "com.lbngttsamples.gtt.app.sof.Shipment.LoadingStart" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.LoadingEnd" == actualEvent.eventType || 
            "com.lbngttsamples.gtt.app.sof.Shipment.CheckIn" == actualEvent.eventType) {
		?"===> entering other events of Shipment";
		if (trackedProcess.executionStatus_code != "COMPLETED") {
			obj = toObject("{}");
			obj.executionStatus_code = "IN_TRANSIT";
			updateTPProperties(trackedProcess, obj);
			
		} 
	}
}

if (needForward) {
    ?"===> forwardCurrentTP to gtt_sof_forwarding-int";
    forwardCurrentTP("gtt_sof_forwarding-int", false, false, true);
}
