/* -------------------------Event Definition for Purchase Order-------------------------------- */
var PURCHASEORDER_EVENT     = "com.lbngttsamples.gtt.app.pof.PurchaseOrder.PurchaseOrderEvent";

/* -------------------------Event Definition for Purchase Order Item--------------------------- */
var GOODSRECEIPT_EVENT      = "com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.GoodsReceipt";
var CONFIRMATION_EVENT      = "com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.ConfirmationEvent";
var DELETION_EVENT          = "com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.DeletionEvent";
var UNDELETION_EVENT        = "com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.UndeletionEvent";

/* -------------------------Event Definition for Inbound Delivery----------------------------- */
var INBDLV_EVENT            = "com.lbngttsamples.gtt.app.pof.InboundDelivery.InboundDeliveryEvent";
/* -------------------------Event Definition for Inbound Delivery Item------------------------ */
var PUTAWAY_EVENT           = "com.lbngttsamples.gtt.app.pof.InboundDeliveryItem.PutAway";
var INBDLVITEM_EVENT        = "com.lbngttsamples.gtt.app.pof.InboundDeliveryItem.InboundDeliveryItemEvent";
var PACKING_EVENT           = "com.lbngttsamples.gtt.app.pof.InboundDeliveryItem.Packing";
/* -------------------------Event Definition for Shipment------------------------------------ */
var POD_EVENT               = "com.lbngttsamples.gtt.app.pof.Shipment.POD";
var DEPARTURE_EVENT         = "com.lbngttsamples.gtt.app.pof.Shipment.Departure";
var ARRIVAL_EVENT           = "com.lbngttsamples.gtt.app.pof.Shipment.Arrival";
var CHECKIN_EVENT           = "com.lbngttsamples.gtt.app.pof.Shipment.CheckIn";
var DELAY_EVENT             = "com.lbngttsamples.gtt.app.pof.Shipment.Delay";
var LOADINGSTART_EVENT      = "com.lbngttsamples.gtt.app.pof.Shipment.LoadingStart";
var LOADINGEND_EVENT        = "com.lbngttsamples.gtt.app.pof.Shipment.LoadingEnd";
var GATEINSTART_EVENT       = "com.lbngttsamples.gtt.app.pof.Shipment.GateInStart";
var GATEINEND_EVENT         = "com.lbngttsamples.gtt.app.pof.Shipment.GateInEnd";
var GATEOUTSTART_EVENT      = "com.lbngttsamples.gtt.app.pof.Shipment.GateOutStart";
var GATEOUTEND_EVENT        = "com.lbngttsamples.gtt.app.pof.Shipment.GateOutEnd";
var UNLOADINGSTART_EVENT    = "com.lbngttsamples.gtt.app.pof.Shipment.UnloadingStart";
var SHIPMENT_EVENT          = "com.lbngttsamples.gtt.app.pof.Shipment.ShipmentEvent";
var UNLOADINGEND_EVENT      = "com.lbngttsamples.gtt.app.pof.Shipment.UnloadingEnd";
var POPU_EVENT              = "com.lbngttsamples.gtt.app.pof.Shipment.POPU";
var STUFFING_EVENT          = "com.lbngttsamples.gtt.app.pof.Shipment.Stuffing";
var UNSTUFFING_EVENT        = "com.lbngttsamples.gtt.app.pof.Shipment.UnStuffing";
var RETURN_EVENT            = "com.lbngttsamples.gtt.app.pof.Shipment.Return";
var RECEIVE_EVENT           = "com.lbngttsamples.gtt.app.pof.Shipment.Receive";
var DELIVERED_EVENT         = "com.lbngttsamples.gtt.app.pof.Shipment.Delivered";
var COUPLING_EVENT          = "com.lbngttsamples.gtt.app.pof.Shipment.Coupling";
var DECOUPLING_EVENT        = "com.lbngttsamples.gtt.app.pof.Shipment.Decoupling";
var OUTFORDELIVERY_EVENT    = "com.lbngttsamples.gtt.app.pof.Shipment.OutForDelivery";

/* -------------------------Track Process Definition -------------------------------------- */
var INBOUNDDELIVERY_TP      = "com.lbngttsamples.gtt.app.pof.InboundDelivery.InboundDelivery";
var PURCHASEORDERITEM_TP    = "com.lbngttsamples.gtt.app.pof.PurchaseOrderItem.PurchaseOrderItem";
var INBDLVITEM_TP           = "com.lbngttsamples.gtt.app.pof.InboundDeliveryItem.InboundDeliveryItem";
var SHIPMENT_TP             = "com.lbngttsamples.gtt.app.pof.Shipment.Shipment";

/* -------------------------Execution Status Code Definition ------------------------------ */
var EXE_SC_NOT_STARTED      = "NOT_STARTED";
var EXE_SC_COMPLETED        = "COMPLETED";
var EXE_SC_IN_TRANSIT       = "IN_TRANSIT";

/* -------------------------Event Status Code Definition --------------------------------- */
var EVENT_SC_REPORTED       = "REPORTED";
var EVENT_SC_EARLY_REPORTED = "EARLY_REPORTED";
var EVENT_SC_LATE_REPORTED  = "LATE_REPORTED";

var actualEventType = actualEvent.eventType;

/*----------------------------------------------Email send begin--------------------------------------------*/
var isEmailEnabled = false; // default to not enabled
if (isEmailEnabled && PURCHASEORDER_EVENT == actualEventType) {
    var userPlaceholders0  = toObject("{}");
    var appPlaceholders0   = toObject("{}");
    var directMailTemp0    = reminderService.generateDirectMailTemplate();
    var purchaseOrderGUID0 = trackedProcess.id;

    userPlaceholders0.recipients1       = "william.mao@sap.com";
    userPlaceholders0.purchaseOrderNo   = trackedProcess.purchaseOrderNo;
    userPlaceholders0.purchaseOrderGUID = purchaseOrderGUID0;

    appPlaceholders0.solutionOwner_BpName = "This is BP name";
    appPlaceholders0.App_Placeholder_1    = "This is APP Place holder 1";

    directMailTemp0.templateId = "invite";
    directMailTemp0.recipients = ["jinming.wang@sap.com", "dexian.li@sap.com", "wilson.wang01@sap.com"];
    directMailTemp0.userdefinedPlaceholders = userPlaceholders0;
    directMailTemp0.applicationdefinedPlaceholders = appPlaceholders0;
    directMailTemp0.post();
}
/*----------------------------------------------Email send end----------------------------------------------*/

/*-----------------------Update Tracked Process/Forward current Tracked Process begin-----------------------*/
/*  Global Varibale Declaration  */
var objTrackedProcess4Update = toObject("{}");
var needForward = false;

// Purchases Order Item Track Process(For Track Purchases Order)
if (trackedProcess != null && PURCHASEORDERITEM_TP == trackedProcess.trackedProcessType) {
    if (DELETION_EVENT == actualEventType || UNDELETION_EVENT == actualEventType ||
        CONFIRMATION_EVENT == actualEventType || GOODSRECEIPT_EVENT == actualEventType) {

        var lastCorrelatedEvent = trackedProcess.getLastCorrelatedEventForCurrentTPIncludeEventTypes(
            [DELETION_EVENT, UNDELETION_EVENT, CONFIRMATION_EVENT, GOODSRECEIPT_EVENT]
        );

        if (lastCorrelatedEvent != null && lastCorrelatedEvent.eventType != trackedProcess.lastReportedEvent) {
            objTrackedProcess4Update = toObject("{}");
            objTrackedProcess4Update.lastReportedEvent = lastCorrelatedEvent.eventType;
            updateTPProperties(trackedProcess, objTrackedProcess4Update);
        }

        needForward = true;
    }
}

// Inbound Delivery Item Track Process(For Track Purchases Order)
else if (trackedProcess != null && INBDLVITEM_TP == trackedProcess.trackedProcessType) {
    if (INBDLVITEM_EVENT == actualEventType) {
        objTrackedProcess4Update = toObject("{}");

        if (trackedProcess.executionStatus_code == null) {
            objTrackedProcess4Update.executionStatus_code = EXE_SC_NOT_STARTED;
        }

        updateTPProperties(trackedProcess, objTrackedProcess4Update);
        needForward = false;

    } else if (trackedProcess.executionStatus_code != EXE_SC_COMPLETED) {
        // Set execution status to EXE_SC_COMPLETED if Delivery Item POD or GoodsReceipt reported.
        if (GOODSRECEIPT_EVENT == actualEventType || POD_EVENT == actualEventType) {
            var plannedEvents = trackedProcess.plannedEvents;
            var allShipmentPODReported = true;
            var found = false;
            var i = 0;
            while (i < plannedEvents.size() && allShipmentPODReported) {
                if (plannedEvents[i].eventType == POD_EVENT) {
                    found = true;
                    if (plannedEvents[i].eventStatus_code != EVENT_SC_REPORTED
                        && plannedEvents[i].eventStatus_code != EVENT_SC_EARLY_REPORTED
                        && plannedEvents[i].eventStatus_code != EVENT_SC_LATE_REPORTED) {

                        allShipmentPODReported = false;
                    }
                }
                i = i + 1;
            }

            if (allShipmentPODReported && found) {
                objTrackedProcess4Update = toObject("{}");
                objTrackedProcess4Update.executionStatus_code = EXE_SC_COMPLETED;
                updateTPProperties(trackedProcess, objTrackedProcess4Update);
            }
            needForward = true;
        }

        if (trackedProcess.executionStatus_code != EXE_SC_COMPLETED &&
            (PUTAWAY_EVENT == actualEventType || PACKING_EVENT == actualEventType ||
                GOODSRECEIPT_EVENT == actualEventType || DEPARTURE_EVENT == actualEventType ||
                ARRIVAL_EVENT == actualEventType ||
                POD_EVENT == actualEventType)) {
            objTrackedProcess4Update = toObject("{}");
            objTrackedProcess4Update.executionStatus_code = EXE_SC_IN_TRANSIT;
            updateTPProperties(trackedProcess, objTrackedProcess4Update);
            needForward = true;
        }
    }

    if (DELETION_EVENT == actualEventType || UNDELETION_EVENT == actualEventType ||
        CONFIRMATION_EVENT == actualEventType || GOODSRECEIPT_EVENT == actualEventType ||
        PUTAWAY_EVENT == actualEventType || PACKING_EVENT == actualEventType || DEPARTURE_EVENT == actualEventType ||
        ARRIVAL_EVENT == actualEventType || LOADINGSTART_EVENT == actualEventType ||
        LOADINGEND_EVENT == actualEventType || POD_EVENT == actualEventType ||
        GATEINSTART_EVENT == actualEventType || GATEINEND_EVENT == actualEventType ||
        GATEOUTSTART_EVENT == actualEventType || GATEOUTEND_EVENT == actualEventType ||
        UNLOADINGSTART_EVENT == actualEventType || UNLOADINGEND_EVENT == actualEventType ||
        STUFFING_EVENT == actualEventType || UNSTUFFING_EVENT == actualEventType ||
        RETURN_EVENT == actualEventType || POPU_EVENT == actualEventType ||
        CHECKIN_EVENT == actualEventType || RECEIVE_EVENT == actualEventType ||
        DELIVERED_EVENT == actualEventType || COUPLING_EVENT == actualEventType ||
        DECOUPLING_EVENT == actualEventType || OUTFORDELIVERY_EVENT == actualEventType) {

        var lastCorrelatedEvent = trackedProcess.getLastCorrelatedEventForCurrentTPIncludeEventTypes(
            [
                DELETION_EVENT, UNDELETION_EVENT, CONFIRMATION_EVENT, GOODSRECEIPT_EVENT,
                PUTAWAY_EVENT, PACKING_EVENT, DEPARTURE_EVENT, ARRIVAL_EVENT,
                LOADINGSTART_EVENT, LOADINGEND_EVENT, POD_EVENT, GATEINSTART_EVENT,
                GATEINEND_EVENT, GATEOUTSTART_EVENT, GATEOUTEND_EVENT, UNLOADINGSTART_EVENT,
                UNLOADINGEND_EVENT, STUFFING_EVENT, UNSTUFFING_EVENT, RETURN_EVENT,
                POPU_EVENT, CHECKIN_EVENT, RECEIVE_EVENT, DELIVERED_EVENT, COUPLING_EVENT,
                DECOUPLING_EVENT, OUTFORDELIVERY_EVENT]
        );

        if (lastCorrelatedEvent != null && lastCorrelatedEvent.id != trackedProcess.lastCorrelatedEventId) {
            objTrackedProcess4Update = toObject("{}");
            objTrackedProcess4Update.lastCorrelatedEventId = lastCorrelatedEvent.id;
            objTrackedProcess4Update.lastReportedEvent = lastCorrelatedEvent.eventType;
            updateTPProperties(trackedProcess, objTrackedProcess4Update);
            needForward = true;
        }

        needForward = true;
    }
} else if (trackedProcess != null && INBOUNDDELIVERY_TP == trackedProcess.trackedProcessType) {
    var plannedEvents = trackedProcess.plannedEvents;
    var shipmentTPs   = trackedProcess.shipmentTPs;
    var trackingIds   = trackedProcess.trackingIds;
    var isPodRelevant = trackedProcess.isPodRelevant;
    var locationId    = trackedProcess.arrivalLocationId;

    objTrackedProcess4Update = toObject("{}");
    var j = 0;
    var foundShipment = false;
    var foundObservedTrackingId = false;
    if (INBDLV_EVENT == actualEventType) {
        var i = 0;
        while (i < plannedEvents.size()) {
            if (DEPARTURE_EVENT == plannedEvents[i].eventType) {
                j = 0;
                foundShipment = false;
                while (j < shipmentTPs.size() && !foundShipment) {
                    if (shipmentTPs[j].firstStop == plannedEvents[i].eventMatchKey) {
                        var k = 0;
                        foundObservedTrackingId = false;
                        while (k < trackingIds.size() && !foundObservedTrackingId) {
                            if (trackingIds[k].observedProcess_id == shipmentTPs[j].shipment_id) {
                                if (plannedEvents[i].plannedBusinessTimestamp != null) {
                                    trackingIds[k].validFrom = plannedEvents[i].plannedBusinessTimestamp.minusMinutes(90);
                                }
                                objTrackedProcess4Update.trackingIds = trackingIds;
                                updateTPProperties(trackedProcess, objTrackedProcess4Update);
                                foundObservedTrackingId = true;
                            }
                            k = k + 1;
                        }
                        foundShipment = true;
                    }
                    j = j + 1;
                }
            }
            i = i + 1;
        }
    }
    
    var v = 0;
    if (ARRIVAL_EVENT == actualEventType || POD_EVENT == actualEventType) {
        var updatedValidTo = false;
        v = 0;
        while (v < shipmentTPs.size() && !updatedValidTo) {
            if (isPodRelevant == "T") {
                if (POD_EVENT == actualEventType &&
                    actualEvent.arrivalLocationId == locationId &&
                    actualEvent.arrivalLocationType == "Plant" &&
                    actualEvent.eventMatchKey == shipmentTPs[v].lastStop) {
                    var k = 0;
                    while (k < trackingIds.size() && !updatedValidTo) {
                        if (trackingIds[k].observedProcess_id == shipmentTPs[v].shipment_id) {
                            trackingIds[k].validTo = actualEvent.actualBusinessTimestamp;
                            objTrackedProcess4Update.trackingIds = trackingIds;
                            updateTPProperties(trackedProcess, objTrackedProcess4Update);
                            updatedValidTo = true;
                        }
                        k = k + 1;
                    }
                }

                if (ARRIVAL_EVENT == actualEventType &&
                    (actualEvent.arrivalLocationId != locationId || actualEvent.arrivalLocationType != "Plant") &&
                    actualEvent.eventMatchKey == shipmentTPs[v].lastStop) {
                    var k = 0;
                    while (k < trackingIds.size() && !updatedValidTo) {
                        if (trackingIds[k].observedProcess_id == shipmentTPs[v].shipment_id) {
                            trackingIds[k].validTo = actualEvent.actualBusinessTimestamp;
                            objTrackedProcess4Update.trackingIds = trackingIds;
                            updateTPProperties(trackedProcess, objTrackedProcess4Update);
                            updatedValidTo = true;
                        }
                        k = k + 1;
                    }
                }
            } else if (isPodRelevant == "F") {
                if (actualEventType == ARRIVAL_EVENT && actualEvent.eventMatchKey == shipmentTPs[v].lastStop) {
                    var k = 0;
                    while (k < trackingIds.size() && !updatedValidTo) {
                        if (trackingIds[k].observedProcess_id == shipmentTPs[v].shipment_id) {
                            trackingIds[k].validTo = actualEvent.actualBusinessTimestamp;
                            objTrackedProcess4Update.trackingIds = trackingIds;
                            updateTPProperties(trackedProcess, objTrackedProcess4Update);
                            updatedValidTo = true;
                        }
                        k = k + 1;
                    }
                }
            }

            v = v + 1;
        }
    }
} else if (trackedProcess != null && SHIPMENT_TP == trackedProcess.trackedProcessType) {
    if (SHIPMENT_EVENT == actualEventType) {
        if (trackedProcess.executionStatus_code == null) {
            objTrackedProcess4Update = toObject("{}");
            objTrackedProcess4Update.executionStatus_code = EXE_SC_NOT_STARTED;
            updateTPProperties(trackedProcess, objTrackedProcess4Update);
        }
    } else if (POD_EVENT == actualEventType || ARRIVAL_EVENT == actualEventType) {
        if (trackedProcess.executionStatus_code != EXE_SC_COMPLETED) {
            var plannedEvents = trackedProcess.plannedEvents;
            var allShipmentPODAndArrivalReported = true;
            var found = false;
            var i = 0;
            while (i < plannedEvents.size() && allShipmentPODAndArrivalReported) {
                if (plannedEvents[i].eventType == POD_EVENT || plannedEvents[i].eventType == ARRIVAL_EVENT) {
                    found = true;
                    if (plannedEvents[i].eventStatus_code != EVENT_SC_REPORTED
                        && plannedEvents[i].eventStatus_code != EVENT_SC_EARLY_REPORTED
                        && plannedEvents[i].eventStatus_code != EVENT_SC_LATE_REPORTED) {
                        allShipmentPODAndArrivalReported = false;
                    }
                }
                i = i + 1;
            }

            if (allShipmentPODAndArrivalReported && found) {
                objTrackedProcess4Update = toObject("{}");
                objTrackedProcess4Update.executionStatus_code = EXE_SC_COMPLETED;
                updateTPProperties(trackedProcess, objTrackedProcess4Update);
            }
        }
    }

    if (trackedProcess.executionStatus_code != EXE_SC_COMPLETED &&
        (DEPARTURE_EVENT == actualEventType || ARRIVAL_EVENT == actualEventType ||
            CHECKIN_EVENT == actualEventType || LOADINGSTART_EVENT == actualEventType ||
            LOADINGEND_EVENT == actualEventType || POD_EVENT == actualEventType)) {
        objTrackedProcess4Update = toObject("{}");
        objTrackedProcess4Update.executionStatus_code = EXE_SC_IN_TRANSIT;
        updateTPProperties(trackedProcess, objTrackedProcess4Update);
    }

    sendTrackingRequest(true);
}

//Forward current Tracked Process
if (needForward) {
    ?"===> forwardCurrentTP to gtt_pof_forwarding-int";
    forwardCurrentTP("gtt_pof_forwarding-int", false, false, true);
}
